	this.besoin_formulaire_texte = ['Choix des tables','Nombres séparés par des tirets'] // Texte, tooltip
	this.besoin_formulaire2_numerique = ['Style de questions',3,'1 : Classique\n2: À trous\n3: Mélangé'] 
